var ajaxRequest = function (type,url,data,successCalback,errorCallback){
    $.ajax({
        type:type,
        url:url,
        data:data,
        success:function (data) {
            successCalback(data);
        },
        error:function (MLHttpRequest, textStatus, errorThro) {
            console.log(MLHttpRequest);
            console.log(textStatus);
        }
    });
}
var setPagination = function (id,totalPages,clickEvent) {
    $('#'+id).twbsPagination({
        prev_text:"上一页",
        next_text:"下一页",
        totalPages: totalPages,
        visiblePages: 5,
        onPageClick: function (event, page) {
            clickEvent(event,page)
            // if(data.enterpriseSnapshot.productsData.currentPage != page){
            //     ajaxRequest("post","numberPageProduct",{page:page,size:5},function (data) {
            //         fillProductHtml(data.productsData.list,true);
            //     });
            // }
        }
    });
};
